import React, { useState } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Registrar los componentes de Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const ChartContainer = ({ title, data }) => {
  const [activeView, setActiveView] = useState('month');

  // Opciones del chart
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      tooltip: {
        mode: 'index',
        intersect: false,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          precision: 0,
        },
      }
    },
  };

  // Cambiar vista del chart (día, mes, año)
  const handleViewChange = (view) => {
    setActiveView(view);
  };

  return (
    <div className="bg-white rounded shadow p-5">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-medium m-0">{title}</h3>
        <div className="flex gap-2">
          <button 
            className={`btn btn-outline text-sm px-3 py-1 ${activeView === 'day' ? 'active' : ''}`}
            onClick={() => handleViewChange('day')}
          >
            Día
          </button>
          <button 
            className={`btn btn-outline text-sm px-3 py-1 ${activeView === 'month' ? 'active' : ''}`}
            onClick={() => handleViewChange('month')}
          >
            Mes
          </button>
          <button 
            className={`btn btn-outline text-sm px-3 py-1 ${activeView === 'year' ? 'active' : ''}`}
            onClick={() => handleViewChange('year')}
          >
            Año
          </button>
        </div>
      </div>

      <div className="w-full h-[300px] relative">
        <Line 
          options={options} 
          data={
            activeView === 'day' ? data.day : 
            activeView === 'month' ? data.month : 
            data.year
          } 
        />
      </div>
    </div>
  );
};

export default ChartContainer;
