import React from 'react';
import { 
  FaClipboardList, 
  FaCheckCircle, 
  FaFileInvoiceDollar, 
  FaMoneyBillWave 
} from 'react-icons/fa';

// Mapeo de iconos por tipo
const iconMap = {
  pending: <FaClipboardList className="text-2xl text-white" />,
  completed: <FaCheckCircle className="text-2xl text-white" />,
  quotes: <FaFileInvoiceDollar className="text-2xl text-white" />,
  income: <FaMoneyBillWave className="text-2xl text-white" />,
};

// Mapeo de colores de fondo para los iconos
const bgColorMap = {
  pending: 'bg-primary',
  completed: 'bg-success',
  quotes: 'bg-warning',
  income: 'bg-info',
};

const StatsCard = ({ title, value, type, trend }) => {
  return (
    <div className="bg-white rounded shadow p-5 flex items-center transition-all duration-300 hover:translate-y-[-5px] hover:shadow-lg">
      <div className={`w-[50px] h-[50px] flex items-center justify-center rounded-xl mr-4 ${bgColorMap[type] || 'bg-primary'}`}>
        {iconMap[type] || <FaClipboardList className="text-2xl text-white" />}
      </div>

      <div className="flex-1">
        <h3 className="text-sm text-gray font-medium m-0">{title}</h3>
        <p className="text-2xl font-semibold m-0 leading-tight">{value}</p>

        {trend && (
          <p className={`text-xs mt-1 flex items-center gap-1 ${trend.positive ? 'text-success' : 'text-danger'}`}>
            <span>{trend.icon}</span>
            <span>{trend.text}</span>
          </p>
        )}
      </div>
    </div>
  );
};

export default StatsCard;
