import React, { useState } from 'react';
import { FaPlus } from 'react-icons/fa';
import QuoteFilters from '../components/quotes/QuoteFilters';
import QuotesTable from '../components/quotes/QuotesTable';
import { quotesData, filterQuotes } from '../utils/quotesMockData';
import { clientData } from '../utils/mockData';

const Quotes = () => {
  const [filters, setFilters] = useState({
    status: 'todos',
    client: 'todos',
    type: 'todos',
    startDate: '',
    endDate: '',
  });

  // Filtrar cotizaciones según los filtros aplicados
  const filteredQuotes = filterQuotes(quotesData, filters);

  // Manejar cambios en los filtros
  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  // Modal para nueva cotización (se implementaría en una versión más completa)
  const handleNewQuote = () => {
    alert('Funcionalidad: Abrir modal para crear nueva cotización');
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <div>
          <h2 className="text-2xl font-bold m-0">Gestión de Cotizaciones</h2>
        </div>
        <div className="flex gap-3">
          <button 
            className="btn btn-primary flex items-center gap-2"
            onClick={handleNewQuote}
          >
            <FaPlus /> Nueva Cotización
          </button>
        </div>
      </div>

      {/* Filtros */}
      <QuoteFilters 
        filters={filters} 
        onFilterChange={handleFilterChange} 
        clients={clientData}
      />

      {/* Tabla de cotizaciones */}
      <div className="bg-white rounded shadow p-4">
        <QuotesTable quotes={filteredQuotes} />
      </div>

      {/* Paginación */}
      <div className="flex justify-center mt-6">
        <div className="flex gap-2">
          <button className="btn btn-outline">
            <i className="fas fa-chevron-left"></i>
          </button>
          <div className="flex gap-1">
            <button className="btn btn-primary">1</button>
            <button className="btn btn-outline">2</button>
            <button className="btn btn-outline">3</button>
          </div>
          <button className="btn btn-outline">
            <i className="fas fa-chevron-right"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default Quotes;
